import { FC } from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm, SubmitHandler } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';

import authService from '../../services/authService';

const schema = yup.object({
    'first_name': yup.string(),
    'last_name': yup.string(),
    'username': yup.string().required(),
    'email': yup.string().required(),
    'password': yup.string().required(),
    'confirm_password': yup.string().required()
}).required();

type Inputs = {
  'first_name': string,
  'last_name': string,
  'username': string,
  'email': string,
  'password': string,
  'confirm_password': string
};

const Register: FC = () => {
    const { register, handleSubmit, formState: { errors } } = useForm<Inputs>({
        resolver: yupResolver(schema)
      });
    const navigate = useNavigate();

    const onSubmit: SubmitHandler<Inputs> = (data) => {
        authService.register(data)
            .then(() => {
               navigate('/login');
            })
            .catch((err) => {
                console.log(err);
            });
    };

    return (
        <section className="form-wrapper">
            <h1 className="title">Register</h1>

            <form className="form" action="POST" onSubmit={handleSubmit(onSubmit)}>
                <div className="form-section">
                    <label htmlFor="first_name">First name</label>
                    <input type="text" id="first_name" {...register("first_name")} />
                </div>

                <div className="form-section">
                    <label htmlFor="last_name">Last name</label>
                    <input type="text" id="last_name" {...register("last_name")} />
                </div>

                <div className="form-section">
                    <label htmlFor="username">Username</label>
                    <input type="text" id="username" {...register("username", { required: true })} />
                    {errors.username && <p className="error">{errors.username.message} test</p>}
                </div>

                <div className="form-section">
                    <label htmlFor="email">Email</label>
                    <input type="email" id="email" {...register("email", { required: true })} />
                </div>

                <div className="form-section">
                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" {...register("password", { required: true })} />
                </div>

                <div className="form-section">
                    <label htmlFor="confirm_password">Confirm Password</label>
                    <input type="password" id="confirm_password" {...register("confirm_password", { required: true })} />
                </div>

                <div className="form-section submit-wrapper">
                    <input type="submit" className="btn primary-btn" value="Register" />
                </div>
            </form>
        </section>
    );
};

export default Register;
